import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../CSS/CreatePost.css'; // Import the CSS file for styling

export default function CreatePost() {
  const [formData, setFormData] = useState({
    title: "",
    filename: "",
    content: "",
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem("userId");
    console.log("UserId:", userId);
    if (!userId) {
      navigate("/login");
      return;
    }

    const newPost = { ...formData, userId };

    axios
      .post("http://localhost:4000/posts", newPost)
      .then(() => {
        alert("Post created successfully!");
        navigate("/dashboard"); // Go back to dashboard after creation
      })
      .catch((err) => console.error(err));
  };

  const handleFeatures = () => {
    navigate("/features"); // Navigate to the Features page
  };

  return (
    <div className="create-post-container"> {/* Centering container */}
      <h2>Create New Post</h2>
      <button onClick={handleFeatures} style={{ marginBottom: '15px', backgroundColor: '#7C5FF0', color: 'white', border: 'none', borderRadius: '4px', padding: '10px', cursor: 'pointer' }}>
        Features
      </button>
      <form onSubmit={handleSubmit} className="create-post-form"> {/* Centering form */}
        <label>Title:</label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
        />
        <label>Filename:</label>
        <input
          type="text"
          name="filename"
          value={formData.filename}
          onChange={handleChange}
        />
        <label>Content:</label>
        <textarea
          name="content"
          value={formData.content}
          onChange={handleChange}
        />
        <button type="submit">Create Post</button>
      </form>
    </div>
  );
}
