import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import Navbar from "../Components/Navbar";

const BACKEND = "http://localhost:4000/login"

const [email, setEmail] = useState()
const [password, setPassword] = useState()
const navigate = useNavigate()

const submit = (e) => {
  e.preventDefault()
  axios.post("http:/localhost:3001/signup", { email, password })
  .then(result => {console.log(result)
  navigate("/login")
  })
  .catch(err => console.log(err))
}


// add token later, make sure that you can identify what that user builds
// goes to the user
// </* form action="http://localhost:4000/login" method = "POST" >

export default function Login() {

  return (
    <div>
      <h2>Login Page</h2>

      <form onSubmit = {submit}>
        <label htmlFor="femail">Email:</label>
        <input 
            type="text" 
            id="femail" 
            name="femail" 
        />
        <label htmlFor="fpassword">Password:</label>
        <input 
            type="text" 
            id="fpassword" 
            name="fpassword"
        />
        <button type="submit">Login</button> 
      </form>
    </div>
  );
}

// when you login to someothing, ask server for cred, 

// if user exists, it gives access token, which works temporarily
// when you go to a new page, u will make request for page using access token. it either 
// exists or it doesnt and then you're signed out.
// save the access token as a cookie

