import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import Navbar from "../Components/Navbar";

const BACKEND = "http://localhost:4000/login";

export default function Login() {
  // Move hooks inside the component
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const submit = (e) => {
    e.preventDefault();

    // Ensure you're using the correct backend URL
    axios
      .post(`${BACKEND}`, { email, password })
      .then((result) => {
        console.log(result);
        navigate("/login"); // Navigates to the login page (might be a typo since you're already on the login page)
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <h2>Login Page</h2>

      <form onSubmit={submit}>
        <label htmlFor="femail">Email:</label>
        <input
          type="text"
          id="femail"
          name="femail"
          value={email} // Controlled input
          onChange={(e) => setEmail(e.target.value)} // Update state on change
        />
        <label htmlFor="fpassword">Password:</label>
        <input
          type="password" // Use password type for security
          id="fpassword"
          name="fpassword"
          value={password} // Controlled input
          onChange={(e) => setPassword(e.target.value)} // Update state on change
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}
